function Sttencode(d){if(jQuery.isEmptyObject(d)){return""}var c="";$.each(d,function(a){c+=(d[a].name==undefined||d[a].name==""?"":filter_str(d[a].name)+"@=")+(d[a].value!=undefined?filter_str(d[a].value):"")+"/"});return c}function Sttdecode(f){if(f==""){return false}var d=f.split("/");var e=new Array();$.each(d,function(b){e[b]=new Object();if(d[b].indexOf("@=")!=-1){var a=d[b].split("@=");e[b].name=de_filter_str(a[0]);e[b].value=de_filter_str(a[1])}else{e[b].name="";e[b].value=de_filter_str(d[b])}});return e}function de_filter_str(b){if(b!=""){b=b.replace(/@A/g,"@");b=b.replace(/@S/g,"/")}return b}function filter_str(b){b=$.trim(b);if(b!=""){b=b.replace(/@/g,"@A");b=b.replace(/\//g,"@S")}return b};